package revision;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     for(int i=1; i<=5; i++) {
    	 
    	 for(int j=1; j<=i; j++) {
    		 
    		 System.out.print("*");
    		 
    	 }
    	 System.out.println();
     }
     
  for(int m=4; m>=1; m--) {
    	 
    	 for(int n=1; n<=m; n++) {
    		 
    		 System.out.print("*");
    		 
    	 }
    	 System.out.println();
	}
	}
}
